import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:5001/api';

// Create axios instance with interceptor to add token to each request
const api = axios.create({
  baseURL: API_BASE_URL
});

// Add token to requests
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

function App() {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showLogin, setShowLogin] = useState(true);
  const [topics, setTopics] = useState([]);
  const [notes, setNotes] = useState([]);
  const [currentView, setCurrentView] = useState('upload'); // 'upload' or 'list'

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      fetchCurrentUser();
      fetchTopics();
      fetchNotes();
    }
  }, []);

  const fetchCurrentUser = async () => {
    try {
      const response = await api.get('/auth/me');
      setUser(response.data.user);
      setIsAuthenticated(true);
    } catch (error) {
      localStorage.removeItem('token');
      setIsAuthenticated(false);
    }
  };

  const fetchTopics = async () => {
    try {
      const response = await api.get('/topics/');
      setTopics(response.data.topics);
    } catch (error) {
      console.error('Error fetching topics:', error);
    }
  };

  const fetchNotes = async () => {
    try {
      const response = await api.get('/upload/list');
      setNotes(response.data.notes);
    } catch (error) {
      console.error('Error fetching notes:', error);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const data = {
      name: formData.get('name'),
      email: formData.get('email'),
      password: formData.get('password')
    };

    try {
      const response = await axios.post(`${API_BASE_URL}/auth/register`, data);
      localStorage.setItem('token', response.data.access_token);
      setUser(response.data.user);
      setIsAuthenticated(true);
      fetchTopics();
      fetchNotes();
    } catch (error) {
      alert(error.response?.data?.error || 'Registration failed');
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const data = {
      email: formData.get('email'),
      password: formData.get('password')
    };

    try {
      const response = await axios.post(`${API_BASE_URL}/auth/login`, data);
      localStorage.setItem('token', response.data.access_token);
      setUser(response.data.user);
      setIsAuthenticated(true);
      fetchTopics();
      fetchNotes();
    } catch (error) {
      alert(error.response?.data?.error || 'Login failed');
    }
  };

  const handleLogout = async () => {
    try {
      await api.post('/auth/logout');
    } catch (error) {
      console.error('Logout error:', error);
    }
    localStorage.removeItem('token');
    setUser(null);
    setIsAuthenticated(false);
    setNotes([]);
    setTopics([]);
  };

  if (!isAuthenticated) {
    return (
      <div style={styles.container}>
        <div style={styles.authContainer}>
          <h1 style={styles.title}>NoteSpace</h1>
          <div style={styles.tabs}>
            <button
              style={{...styles.tab, ...(showLogin ? styles.activeTab : {})}}
              onClick={() => setShowLogin(true)}
            >
              Login
            </button>
            <button
              style={{...styles.tab, ...(!showLogin ? styles.activeTab : {})}}
              onClick={() => setShowLogin(false)}
            >
              Register
            </button>
          </div>
          {showLogin ? (
            <form onSubmit={handleLogin} style={styles.form}>
              <input
                type="email"
                name="email"
                placeholder="Email"
                required
                style={styles.input}
              />
              <input
                type="password"
                name="password"
                placeholder="Password"
                required
                style={styles.input}
              />
              <button type="submit" style={styles.button}>Login</button>
            </form>
          ) : (
            <form onSubmit={handleRegister} style={styles.form}>
              <input
                type="text"
                name="name"
                placeholder="Name"
                required
                style={styles.input}
              />
              <input
                type="email"
                name="email"
                placeholder="Email"
                required
                style={styles.input}
              />
              <input
                type="password"
                name="password"
                placeholder="Password (min 6 characters)"
                required
                minLength={6}
                style={styles.input}
              />
              <button type="submit" style={styles.button}>Register</button>
            </form>
          )}
        </div>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h1 style={styles.title}>NoteSpace</h1>
        <div style={styles.headerRight}>
          <span style={styles.userName}>Welcome, {user?.name}</span>
          <button onClick={handleLogout} style={styles.logoutButton}>Logout</button>
        </div>
      </header>

      <nav style={styles.nav}>
        <button
          style={{...styles.navButton, ...(currentView === 'upload' ? styles.activeNavButton : {})}}
          onClick={() => setCurrentView('upload')}
        >
          Upload
        </button>
        <button
          style={{...styles.navButton, ...(currentView === 'list' ? styles.activeNavButton : {})}}
          onClick={() => setCurrentView('list')}
        >
          File List
        </button>
      </nav>

      <main style={styles.main}>
        {currentView === 'upload' && (
          <UploadPage 
            topics={topics} 
            onUpload={fetchNotes} 
            onCreateTopic={(topic) => {
              setTopics([...topics, topic]);
            }}
          />
        )}
        {currentView === 'list' && (
          <FileListView notes={notes} />
        )}
      </main>
    </div>
  );
}

function UploadPage({ topics, onUpload, onCreateTopic }) {
  const [selectedTopic, setSelectedTopic] = useState('');
  const [dragActive, setDragActive] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [uploadStatus, setUploadStatus] = useState('');
  const [showTopicForm, setShowTopicForm] = useState(false);
  const [newTopicName, setNewTopicName] = useState('');
  const [creatingTopic, setCreatingTopic] = useState(false);

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setDragActive(true);
    } else if (e.type === 'dragleave') {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleFile(e.dataTransfer.files[0]);
    }
  };

  const handleFileInput = (e) => {
    if (e.target.files && e.target.files[0]) {
      handleFile(e.target.files[0]);
    }
  };

  const handleCreateTopic = async (e) => {
    e.preventDefault();
    if (!newTopicName.trim()) {
      setUploadStatus('Topic name cannot be empty');
      return;
    }

    setCreatingTopic(true);
    try {
      const token = localStorage.getItem('token');
      console.log('Token being sent:', token ? token.substring(0, 20) + '...' : 'No token');

      const response = await api.post('/topics/', {
        name: newTopicName.trim()
      });
      onCreateTopic(response.data.topic);
      setNewTopicName('');
      setShowTopicForm(false);
      setSelectedTopic(response.data.topic.id);
      setUploadStatus('Topic created successfully!');
      setTimeout(() => setUploadStatus(''), 2000);
    } catch (error) {
      console.error('Topic creation error:', error.response?.data || error.message);
      setUploadStatus(error.response?.data?.error || 'Failed to create topic');
    } finally {
      setCreatingTopic(false);
    }
  };

  const handleFile = async (file) => {
    if (!selectedTopic) {
      setUploadStatus('Please select a topic first');
      return;
    }

    const allowedTypes = ['application/pdf', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document', 'text/plain'];
    if (!allowedTypes.includes(file.type) && !file.name.match(/\.(pdf|docx|txt)$/i)) {
      setUploadStatus('Invalid file type. Only PDF, DOCX, and TXT files are allowed.');
      return;
    }

    setUploading(true);
    setUploadStatus('Uploading...');

    const formData = new FormData();
    formData.append('file', file);
    formData.append('topic_id', selectedTopic);

    try {
      const response = await api.post('/upload/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setUploadStatus('Upload successful!');
      setTimeout(() => {
        setUploadStatus('');
        setSelectedTopic('');
      }, 2000);
      onUpload();
    } catch (error) {
      setUploadStatus(error.response?.data?.error || 'Upload failed');
    } finally {
      setUploading(false);
    }
  };

  return (
    <div style={styles.uploadContainer}>
      <h2 style={styles.sectionTitle}>Upload File</h2>

      <div style={styles.formGroup}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '0.5rem' }}>
          <label style={styles.label}>Select Topic:</label>
          <button
            type="button"
            onClick={() => setShowTopicForm(!showTopicForm)}
            style={styles.createTopicButton}
          >
            {showTopicForm ? 'Cancel' : '+ New Topic'}
          </button>
        </div>
        {showTopicForm ? (
          <form onSubmit={handleCreateTopic} style={{ display: 'flex', gap: '0.5rem' }}>
            <input
              type="text"
              value={newTopicName}
              onChange={(e) => setNewTopicName(e.target.value)}
              placeholder="Topic name"
              style={styles.input}
              disabled={creatingTopic}
              required
            />
            <button type="submit" style={styles.button} disabled={creatingTopic}>
              Create
            </button>
          </form>
        ) : (
          <select
            value={selectedTopic}
            onChange={(e) => setSelectedTopic(e.target.value)}
            style={styles.select}
            required
          >
            <option value="">-- Select a topic --</option>
            {topics.map(topic => (
              <option key={topic.id} value={topic.id}>
                {topic.name}
              </option>
            ))}
          </select>
        )}
      </div>

      <div
        style={{
          ...styles.dropZone,
          ...(dragActive ? styles.dropZoneActive : {})
        }}
        onDragEnter={handleDrag}
        onDragLeave={handleDrag}
        onDragOver={handleDrag}
        onDrop={handleDrop}
      >
        <input
          type="file"
          id="file-input"
          onChange={handleFileInput}
          accept=".pdf,.docx,.txt"
          style={{ display: 'none' }}
          disabled={uploading}
        />
        <label htmlFor="file-input" style={styles.dropZoneLabel}>
          {dragActive ? (
            <span>Drop file here</span>
          ) : (
            <span>
              <strong>Click to upload</strong> or drag and drop<br />
              PDF, DOCX, or TXT files only
            </span>
          )}
        </label>
      </div>

      {uploadStatus && (
        <div style={{
          ...styles.statusMessage,
          ...(uploadStatus.includes('success') ? styles.successMessage : styles.errorMessage)
        }}>
          {uploadStatus}
        </div>
      )}
    </div>
  );
}

function FileListView({ notes }) {
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString();
  };

  const formatFileSize = (bytes) => {
    if (bytes < 1024) return bytes + ' B';
    if (bytes < 1024 * 1024) return (bytes / 1024).toFixed(2) + ' KB';
    return (bytes / (1024 * 1024)).toFixed(2) + ' MB';
  };

  return (
    <div style={styles.listContainer}>
      <h2 style={styles.sectionTitle}>Uploaded Files</h2>
      {notes.length === 0 ? (
        <p style={styles.emptyMessage}>No files uploaded yet.</p>
      ) : (
        <table style={styles.table}>
          <thead>
            <tr>
              <th style={styles.th}>Filename</th>
              <th style={styles.th}>Topic</th>
              <th style={styles.th}>Uploader</th>
              <th style={styles.th}>Size</th>
              <th style={styles.th}>Uploaded At</th>
            </tr>
          </thead>
          <tbody>
            {notes.map(note => (
              <tr key={note.id}>
                <td style={styles.td}>{note.original_filename}</td>
                <td style={styles.td}>{note.topic_name || `Topic #${note.topic_id}`}</td>
                <td style={styles.td}>{note.uploader_name || 'Unknown'}</td>
                <td style={styles.td}>{formatFileSize(note.file_size)}</td>
                <td style={styles.td}>{formatDate(note.uploaded_at)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

